// @flow
import React from 'react';
import { connect } from 'react-redux';
import Notification from '@ncigdc/uikit/Notification';

const NotificationContainer = ({ notification }) => (
  <Notification id={notification.id} action={notification.action}>
    {notification.component}
  </Notification>
);

export default
connect(state => ({ notification: state.notification }))(NotificationContainer);
