/* @flow */

import React from 'react';

export type TProps = {|
  params: Object,
  prefix?: string,
  total: number,
|};

const Sizes = (props: TProps) => {
  const prfOff = [props.prefix, 'offset'].filter(Boolean).join('_');
  const prfSize = [props.prefix, 'size'].filter(Boolean).join('_');

  const start = props.params[prfOff] + 1;
  const end = Math.min(props.params[prfOff] + props.params[prfSize], props.total);

  return (
    <div>
      Showing {start} - {end} of {props.total}
    </div>
  );
};

export default Sizes;
