/* @flow */

import React from 'react';

import Dropdown from '@ncigdc/uikit/Dropdown';
import Link from '@ncigdc/components/Links/Link';

export type TProps = {|
  prfSize: string,
  size: number,
  sizes?: Array<number>,
|};

const Sizes = (props: TProps) => {
  const sizes = props.sizes || [5, 10, 20];

  return (
    <span>
      <Dropdown
        selected={props.size}
        dropdownStyle={{
          minWidth: '40px',
          width: '40px',
        }}
        itemStyle={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        {sizes.map(x => (
          props.size === x
            ? <span key={x}>{x}</span>
            : <Link key={x} merge query={{ [props.prfSize]: x }}>{x}</Link>
        ))}
      </Dropdown>
    </span>
  );
};

export default Sizes;
