// @flow

import React from 'react';

type TProps = {
  hits: {
    pagination: {
      total: number,
    },
  },
};

const ProjectsCount = (props: TProps) => (
  <span>
    {props.hits.pagination.total > 0
      ? props.hits.pagination.total.toLocaleString()
      : <span className="fa fa-spinner fa-spin" />
    }
  </span>
);

export default ProjectsCount;
