/* @flow */

import React from 'react';
import Relay from 'react-relay';

import { parseIntParam, parseFilterParam } from '@ncigdc/utils/uri';

import ProjectTable from './ProjectTable';
import ProjectAggregations from './ProjectAggregations';

export type TProps = {
  viewer: {
    projects: {
      aggregations: string,
      hits: string,
    },
  },
};

export const ProjectsPageComponent = (props: TProps) => (
  <div style={{ flex: 1 }}>
    <aside style={{ flex: '0 0 12em' }}>
      <ProjectAggregations aggregations={props.viewer.projects.aggregations} />
    </aside>
    <section style={{ flex: 1 }}>
      <ProjectTable hits={props.viewer.projects.hits} />
    </section>
  </div>
);

export const ProjectsPageQuery = {
  initialVariables: {
    projects_first: 20,
    offset: 0,
    filters: null,
    sort: 'summary.case_count',
  },
  prepareVariables: q => {
    console.log(q);
    const r = {
      projects_first: parseIntParam(q.projects_first, 20),
      offset: parseIntParam(q.projects_offset, 0),
      filters: parseFilterParam(q.filters, null),
      sort: q.projects_sort || 'summary.case_count',
    };
    console.log(r);
    return r;
  },
  fragments: {
    viewer: () => Relay.QL`
      fragment on Root {
        projects {
          aggregations(filters: $filters) {
            ${ProjectAggregations.getFragment('aggregations')}
          }
          hits(first: $projects_first offset: $offset, filters: $filters) {
            ${ProjectTable.getFragment('hits')}
          }
        }
      }
    `,
  },
};

const ProjectsPage = Relay.createContainer(
  ProjectsPageComponent,
  ProjectsPageQuery
);

export default ProjectsPage;
