/* @flow */

import React from 'react';
import Relay from 'react-relay';

import SearchPage from '@ncigdc/components/SearchPage';
import TabbedAggregations from '@ncigdc/components/TabbedAggregations';

import CaseTable from './CaseTable';
import CaseAggregations from './CaseAggregations';
import FileTable from './FileTable';
import FileAggregations from './FileAggregations';

export type TProps = {
  viewer: {
    cases: {
      aggregations: string,
      hits: string,
    },
    files: {
      aggregations: string,
      hits: string,
    },
  },
};

export const RepositoryPageComponent = (props: TProps) => (
  <div>
    <SearchPage
      Facets={
        <TabbedAggregations
          defaultDocType='cases'
          aggregations={[
            {
              docType: 'cases',
              component: <CaseAggregations aggregations={props.viewer.cases.aggregations} />,
            },
            {
              docType: 'files',
              component: <FileAggregations aggregations={props.viewer.files.aggregations} />,
            },
          ]}
        />
      }
      // Results={<CaseTable hits={props.viewer.cases.hits} />}
    />


    {/* <SearchPage
      Facets={<FileAggregations aggregations={props.viewer.files.aggregations} />}
      Results={<FileTable hits={props.viewer.files.hits} />}
    />
    <SearchPage
      Facets={}
      Results={<CaseTable hits={props.viewer.cases.hits} />}
    /> */}
  </div>
);

export const RepositoryPageQuery = {
  initialVariables: {
    cases_offset: null,
    cases_size: null,
    cases_sort: null,
    files_offset: null,
    files_size: null,
    files_sort: null,
    filters: null,
  },
  fragments: {
    viewer: () => Relay.QL`
      fragment on Root {
        cases {
          aggregations(filters: $filters) {
            ${CaseAggregations.getFragment('aggregations')}
          }
          hits(first: $cases_size offset: $cases_offset, filters: $filters) {
            ${CaseTable.getFragment('hits')}
          }
        }
        files {
          aggregations(filters: $filters) {
            ${FileAggregations.getFragment('aggregations')}
          }
          hits(first: $files_size offset: $files_offset, filters: $filters) {
            ${FileTable.getFragment('hits')}
          }
        }
      }
    `,
  },
};

const RepositoryPage = Relay.createContainer(
  RepositoryPageComponent,
  RepositoryPageQuery
);

export default RepositoryPage;
