// @flow
/* eslint-disable fp/no-mutation, fp/no-let */

import React from 'react';

let theme = {};

export const setTheme = (version = 'active', custom = {}) => {
  theme = {
    ...require(`./versions/${version}`).default,
    ...custom,
  }
};

export const getTheme = () => theme;

export const withTheme = Wrapped => props => <Wrapped theme={getTheme()} {...props} />;
