// @flow
import React from 'react';
import InternalLink from 'components/InternalLink';

export type Bucket = {
  key: string,
  doc_count: number
}

type TermFacetProps = {
  field: string,
  buckets: [Bucket],
  pathname: string,
  params: {
    filters: {
      content: {
        op: string,
        content: {
          field: string,
          value: [string],
        },
      }
    }
  }
}

const TermFacet = (props: TermFacetProps) => {
  const wrapAnd = {
    op: 'and',
    content: [],
  };
  const dotField = props.field.replace(/__/g, '.');
  return (
    <div>
      <h3>{dotField}</h3>
      <div>
        {props.buckets.map(bucket => {
          const mergeFilters = (filters, b) => (
            {
              op: 'and',
              content: [
                // $FlowIssue
                ...filters.content,
                {
                  op: 'in',
                  content: {
                    field: dotField,
                    value: [b.key],
                  },
                },
              ],
            }
          );

          const mergedFilters = mergeFilters(props.params.filters || wrapAnd, bucket);
          return (
            <div key={bucket.key.replace(' ', '_')}>
              <InternalLink
                merge
                query={{
                  offset: 0,
                  filters: JSON.stringify(mergedFilters),
                }}
              >
                {bucket.key}
              </InternalLink>
              <span>{bucket.doc_count}</span>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default TermFacet;
