/* @flow */
/* eslint fp/no-class:0 */

import React from 'react';
import Relay from 'react-relay';

import FilesPage from 'containers/FilesPage';
import { prepareViewerParams } from 'routes/utils';
import { viewerQuery } from 'routes/queries';

class FilesRoute extends Relay.Route {
  static routeName = 'FilePageRoute';
  static queries = viewerQuery;
  static prepareParams = prepareViewerParams;
}

export default (props: any) => (
  <Relay.Renderer
    Container={FilesPage}
    queryConfig={new FilesRoute(props)}
    environment={Relay.Store}
  />
);
